package com.example.util.payment.ios;


import com.alibaba.fastjson.JSONArray;
import com.alibaba.fastjson.JSONObject;

import java.io.*;
import java.net.HttpURLConnection;
import java.net.MalformedURLException;
import java.net.ProtocolException;
import java.net.URL;
import java.util.Locale;

/**
 * 2013-8-19 author:wanlingzhi
 */
public class IosPayUtil {
	public static String check(final String receipt, final String transaction_id, final boolean sendbox) {
		try {
			String url = "https://buy.itunes.apple.com/verifyReceipt";
			if (sendbox) {
				url = "https://sandbox.itunes.apple.com/verifyReceipt";	//沙盒测试
			}
			URL dataUrl = new URL(url);
			HttpURLConnection con = (HttpURLConnection) dataUrl.openConnection();
			con.setRequestMethod("POST");
			con.setRequestProperty("content-type", "text/json");
			con.setRequestProperty("Proxy-Connection", "Keep-Alive");
			con.setDoOutput(true);
			con.setDoInput(true);
			OutputStreamWriter out = new OutputStreamWriter(con.getOutputStream());
			String str = String.format(Locale.CHINA, "{\"receipt-data\":\""+ receipt + "\"}");
			out.write(str);
			out.flush();
			out.close();
			InputStream is = con.getInputStream();
			BufferedReader reader = new BufferedReader(new InputStreamReader(is));
			StringBuffer sb = new StringBuffer();
			String line = null;
			while ((line = reader.readLine()) != null) {
				sb.append(line);
			}
			JSONObject json = (JSONObject) JSONObject.parse(sb.toString());
			if ("0".equals(json.getString("status"))&& json.get("receipt") instanceof JSONObject) {
				json = json.getJSONObject("receipt");
				if (json.get("in_app") instanceof JSONArray) {
					JSONArray array = json.getJSONArray("in_app");
					if (array.size() > 0) {
						JSONObject obj;
						String transid;
						for (int i = 0; i < array.size(); i++) {
							obj = array.getJSONObject(i);
							transid = obj.getString("transaction_id");
							if (transid != null&& transid.equals(transaction_id)) {
								return obj.getString("product_id");
							}
						}
					}
				}
			}
		} catch (MalformedURLException e) {
			e.printStackTrace();
		} catch (ProtocolException e) {
			e.printStackTrace();
		} catch (IOException e) {
			e.printStackTrace();
		}
		return "";
	}

	public static void main(String[] args) {
		String receipt ="";
		System.out.println(IosPayUtil.check(receipt, "1000000190727113", true));
	}
}
